INSERT INTO news (title, content, image_url, published) VALUES
('حملة تنظيف شاملة في شاطئ سيدي لخضر', 'ستنظم حملة تنظيف شاملة في شاطئ سيدي لخضر يوم 15 أوت 2025 بمشاركة المواطنين والجمعيات المحلية. الهدف من هذه الحملة هو المحافظة على نظافة الشاطئ وحماية البيئة البحرية.', 'https://images.pexels.com/photos/1105766/pexels-photo-1105766.jpeg?auto=compress&cs=tinysrgb&w=400&h=250&fit=crop', true),
('تهنئة بمناسبة عيد الفطر المبارك', 'بمناسبة عيد الفطر المبارك، تتقدم البلدية بأحر التهاني للمواطنين وتتمنى لهم عيداً مباركاً وكل عام وأنتم بخير. كما تعلن البلدية عن إجازة رسمية لمدة ثلاثة أيام.', 'https://images.pexels.com/photos/1105766/pexels-photo-1105766.jpeg?auto=compress&cs=tinysrgb&w=400&h=250&fit=crop', true),
('تهيئة طرقات حي وسط المدينة', 'انطلاق أشغال تهيئة طرقات حي وسط المدينة ضمن برنامج التطوير الحضري للبلدية. المشروع يشمل إعادة تعبيد الطرق وتحسين الإضاءة العمومية وإنشاء أرصفة جديدة.', 'https://images.pexels.com/photos/1105766/pexels-photo-1105766.jpeg?auto=compress&cs=tinysrgb&w=400&h=250&fit=crop', true);
INSERT INTO projects (title, description, status, year, published) VALUES
('تهيئة طرقات حي وسط المدينة', 'مشروع شامل لتهيئة وتطوير طرقات حي وسط المدينة لتحسين حركة المرور وتوفير بيئة آمنة للمشاة. يشمل المشروع إعادة تعبيد الطرق وتحسين الإضاءة.', 'ongoing', 2025, true),
('مشروع السوق المغطى الجديد', 'إطلاق مشروع السوق المغطى الجديد لتوفير مساحة تجارية حديثة للتجار والمواطنين. السوق سيضم محلات تجارية متنوعة ومرافق خدمية.', 'planned', 2025, true),
('تطوير الشاطئ السياحي', 'مشروع تطوير الشاطئ السياحي لتعزيز السياحة الشاطئية في المنطقة. يشمل إنشاء مرافق سياحية وتحسين الخدمات المقدمة للزوار.', 'planned', 2025, true);
